import React, { useState } from 'react';
import Axios from "axios";



const CriteriosEvaluacion = () => {


    const [titulo, setTitulo] = useState ("");
    const [descripcion, setDescripcion] = useState ("");

    const formularioEvaluacion = () => {
        Axios.post('http://localhost:4000/add-evaluacion', {
          Titulo: titulo, 
          Descripcion: descripcion,
        }).then((response) => {
            console.log(response)
          });
    }

    return (
        <div>
            <div className="formulario">
                <label >Titulo</label>
                <input type="text"  placeholder="Escribe un titulo..."/>

                {/* <label for="apellidos">Apellidos</label>
                <input type="text" name="apellidos" id="apellidos" placeholder="Apellidos"/>

                <label for="email" >Email</label>
                <input type="email" name="email" id="email" placeholder="Email" required /> */}

                <label >Descripción</label>
                <textarea  placeholder="Ingresa el criterio de evaluación... " maxlength="300"></textarea>
                
                <button className="custom-btn btn-11" > Guardar </button>
            </div>
        </div>
    )
}

export default CriteriosEvaluacion;
