                <input type="text" name="titulo" id="titulo" placeholder="Escribe un titulo..."/>

                {/* <label for="apellidos">Apellidos</label>
                <input type="text" name="apellidos" id="apellidos" placeholder="Apellidos"/>

                <label for="email" >Email</label>
                <input type="email" name="email" id="email" placeholder="Email" required /> */}

                <label for="descripcion">Descripción</label>
                <textarea name="descripcion" for="descripcion" placeholder="Ingresa el criterio de evaluación... " maxlength="300"></textarea>
                
                <button className="custom-btn btn-11" > Guardar </button>
            </div>
        </div>
    )
}

export default CriteriosEvaluacion;
